import React from "react";
import { Route, Routes, useNavigate } from "react-router-dom";
import Header from "./Header.jsx";
import Main from './Main.jsx';
import Footer from './Footer.jsx';
import ImagePopup from "./ImagePopup.jsx";
import CurrentUserContext from "../contexts/CurrentUserContext.js";
import api from "../utils/api.js";
import EditProfilePopup from "./EditProfilePopup.jsx";
import EditAvatarPopup from "./EditAvatarPopup.jsx";
import AddPlacePopup from "./AddPlacePopup.jsx";
import DeletePopupCard from "./DeleteCardPopup.jsx";
import ProtectedRouteElement from "./ProtectedRoute";
import InfoTooltip from "./InfoTooltip";
import Login from "./Login.jsx";
import Register from "./Register.jsx";
import {register, authorize, getContent} from "../utils/auth.js"

function App() {

  const [isEditProfilePopupOpen, setEditPopupOpen] = React.useState(false);
  const [isAddPlacePopupOpen, setAddPopupOpen] = React.useState(false);
  const [isEditAvatarPopupOpen, setAvatarPopupOpen] = React.useState(false);
  const [selectedCard, setSelectedCard] = React.useState({});
  const [isDeletePopup, setDeleteCardPopup] = React.useState(false);
  const [isImagePopup, setImagePopup] = React.useState(false);
  const [currentUser, setCurrentUser] = React.useState({});
  const [cards, setCards] = React.useState([]);
  const [deleteCardId, setDeleteCardId] = React.useState("");

  const [isInfoTooltipOpen, setInfoTooltipOpen] = React.useState(false);
  const [isRegister, setRegister] = React.useState(false);
  const [loggedIn, setLoggedIn] = React.useState(false);
  const [email, setEmail] = React.useState();

  const navigate = useNavigate();

  React.useEffect(() => {
    if (loggedIn) {
      Promise.all([api.getInfo(), api.getCards()])
      .then(([dataUser, dataCard]) => {
        setCurrentUser(dataUser);
        setCards(dataCard);
      }) 
      .catch((error => console.error(`Ошибка ${error}`)))
    }
  }, [loggedIn])

  //Проверка токена при загрузке страницы
  // React.useEffect(() => {
  //   const token = localStorage.getItem('token');
  //   // если у пользователя есть токен в localStorage, 
  //   // функция проверит, действующий он или нет
  //   if (token){
  //     getContent(token)
  //       .then((res) => {
  //         if (res) {
  //           setLoggedIn(true);
  //           setEmail(res.data.email);
  //           navigate('/', {replace: true})
  //         }
  //       })
  //       .catch(console.error);
  //   }
  // }, []);

  //Обработка запроса на регистрацию
  function handleRegister(email, password) {
    register(email, password)
    .then((res) => {
      if (res) {
        setRegister(true);
        infoTooltipPopupOpen()
        navigate('/sign-in', {replace: true});
      } else {
        infoTooltipPopupOpen()
        setRegister(false);
      }
    })
    .catch(() => {
      infoTooltipPopupOpen()
      setRegister(false);
      console.error();
    })
  }

  //Изменение статуса логина
  function handleLoggedIn() {
    setLoggedIn(true);
  }

  function infoTooltipPopupOpen() {
    setInfoTooltipOpen(true);
  }

  //Обработка запроса на авторизацию
  function handleLogin(email, password) {
    authorize(email, password)
    .then((data) => {
      if (data.token) {
        setEmail(email);
        handleLoggedIn();
        navigate('/', {replace: true})
      }
    })
    .catch(() => {
      setLoggedIn(false);
      infoTooltipPopupOpen()
      console.error();
    })
  }

  //Выход из системы
  function handleLogout() {
    setLoggedIn(false);
    localStorage.removeItem("token");
    navigate("/sign-in");
  }

  function handleEditProfileClick() {
    setEditPopupOpen(true)
  }

  function handleAddPlaceClick() {
    setAddPopupOpen(true)
  }

  function handleEditAvatarClick() {
    setAvatarPopupOpen(true)
  }

  function handleCardClick(card) {
    setSelectedCard(card);
    setImagePopup(true);
  }

  function handleDeleteCard(cardId) {
    setDeleteCardPopup(true);  
    setDeleteCardId(cardId) 
  }

  function closeAllPopups() { 
    setEditPopupOpen(false)
    setAvatarPopupOpen(false)
    setAddPopupOpen(false)
    setDeleteCardPopup(false)
    setImagePopup(false)
    setInfoTooltipOpen(false)
  }

  function handleCardLike (card) {
    const isLike = card.likes.some((element) => currentUser._id === element._id);
    if (isLike) {
      api.deleteLike(card._id)
      .then((res) => {
        setCards((state) =>
          state.map((c) => (c._id === card._id ? res : c))
        )
      })
      .catch(error => console.log(`Ошибка снятия лайка ${error}`));
    } else {
      api.putLike(card._id, true)
      .then((res) => {
        setCards((state) =>
          state.map((c) => (c._id === card._id ? res : c))
        )
      })
      .catch(error => console.log(`Ошибка постановки лайка ${error}`)); 
    }
  }

  function handleDeleteSubmit() {
    api.deleteCard(deleteCardId)
    .then(() => {
      setCards((state) =>
        state.filter((card) => {
          return card._id !== deleteCardId;
        })
      );
      closeAllPopups();
    })
    .catch((error => console.error(`Ошибка удаления карточки ${error}`))) 
  }

  function handleUpdateUser(dataUser, reset) {
    api.setUserInfo(dataUser)
    .then(res => {
      setCurrentUser(res);
      closeAllPopups()
      reset()
    })
    .catch((error) => console.log(`Ошибка редактирования профиля ${error}`));
  }

  function handleUpdateAvatar(dataUser, reset){
    api.setAvatar(dataUser)
    .then(res => {
      setCurrentUser(res);
      closeAllPopups()
      reset()
    })
    .catch((error) => console.log(`Ошибка обновления аватара ${error}`));
  }

  function handleAddPlace(cardInfo, reset){
    api.addCard(cardInfo)
    .then((res) => {
      setCards([res, ...cards]);
      closeAllPopups()
      reset()
    })
    .catch((error) => console.log(`Ошибка добавления карточки ${error}`));
  }

  return (
    <CurrentUserContext.Provider value={currentUser}>
      <div className="page__content">
        <Header loggedIn={loggedIn} onLogout={handleLogout} email={email}/>
        <Routes>
          <Route path="/sign-up" element={<Register onRegister={handleRegister}/>}/>
          <Route path="/sign-in" element={<Login onLogin={handleLogin}/>}/>
          <Route
            path="/"
            element={<ProtectedRouteElement 
              loggedIn={loggedIn} 
              element={Main}
              onEditAvatar={handleEditAvatarClick}
              onEditProfile={handleEditProfileClick}
              onAddPlace={handleAddPlaceClick}
              onCardClick={handleCardClick}
              onDeleteCard={handleDeleteCard}
              onCardLike={handleCardLike}
              cards={cards}
            />} 
          />
        </Routes>
        <Footer/>
        <InfoTooltip
          name="info"
          isOpen={isInfoTooltipOpen}
          onClose={closeAllPopups}
          isConfirmed={isRegister}
        />
        <ImagePopup
          card={selectedCard}
          isOpen={isImagePopup}
          onClose={closeAllPopups}
        /> 
        <EditProfilePopup
          isOpen={isEditProfilePopupOpen}
          onClose={closeAllPopups}  
          onUpdateUser={handleUpdateUser} 
        />
        <AddPlacePopup
          isOpen={isAddPlacePopupOpen}
          onClose={closeAllPopups}
          onAddPlace={handleAddPlace}
        />
        <EditAvatarPopup
          isOpen={isEditAvatarPopupOpen}
          onClose={closeAllPopups}
          onUpdateAvatar={handleUpdateAvatar}
        />
        <DeletePopupCard
          isOpen={isDeletePopup}
          onClose={closeAllPopups}
          onCardDelete={handleDeleteSubmit}
        />
      </div>
    </CurrentUserContext.Provider>
  );
}

export default App;
